  .container {
    @apply px-6 mx-auto;
  }

  nav {
    @apply flex flex-col py-2 
    sm:flex-row sm:justify-between 
    sm:items-center;
  }

  .brand__link {
    @apply text-2xl font-semibold text-white hover:text-gray-300;
  }

  .sign {
    @apply flex items-center mt-2 -mx-2 sm:mt-0;

    &-in__link {
      @apply px-3 py-1 text-sm font-semibold text-white transition-colors duration-200 transform border-2 rounded-md hover:bg-gray-700;
    }

    &-up__link {
      @apply px-3 py-2 mx-2 text-sm font-semibold text-white transition-colors duration-200 transform bg-black rounded-md hover:bg-gray-800;
    }
  }

  .hero {
